// Import the hook from the library
import { useSearchBox } from 'react-instantsearch';

// Create a custom search box component
function CustomSearchBox(props) {
    // Use the hook and get the state and functions
    const { query, refine, clear, isSearchStalled } = useSearchBox(props);

    // Return some JSX elements
    return (
        <div>
            <input
                type="text"
                placeholder="Search..."
                value={query}
                onChange={(e) => refine(e.target.value)} // Refine the search query on input change
            />
            <button onClick={clear}>Clear</button> // Clear the search query on button click
            {isSearchStalled && <span>Loading...</span>} // Show a loading indicator if the search is stalled
        </div>
    );
}

// Render the custom search box component in your app
function Searchbox() {
    return (
        <div>
            <CustomSearchBox
                // Pass any props you want to the hook
                translations={{
                    placeholder: 'Enter a product name',
                }}
            />
        </div>
    );
}
export default CustomSearchBox;
