// import useRef, useState, useCallback, useEffect
import { useRef, useState, useCallback, useEffect } from "react";
// import Webcam component
import Webcam from "react-webcam";

const CustomWebcam = () => {
  const webcamRef = useRef(null); // create a webcam reference
  const [imgSrc, setImgSrc] = useState(null); // initialize it

  // Captures Photo
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
  }, [webcamRef]);

  const retake = () => {
    setImgSrc(null);
  };

  // Function for sending photo
  const sendImage = async () => {
    // convert the image to a base64 string
    const data = imgSrc.replace(/^data:image\/jpeg;base64,/, "");
    const response = await fetch("https://sam141.brighton.domains/cloudvisionapi", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ base64: data }),
    });
    // handle the response
    if (response.ok) {
      console.log("Image sent successfully");
      console.log(response);
    } else {
      console.error("Error sending image");
      console.log(response);
    }
  };

  return (
    <div className="container">
      {imgSrc ? (
        <img src={imgSrc} alt="webcam" />
      ) : (
        <Webcam
          //height={960}
          //width={540}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          screenshotQuality={0.8}
        />
      )}
      <div className="btn-container">
        <button>BrowsePhotos</button>
        {imgSrc ? (
          <>
            <button onClick={retake}>Retake photo</button>
            <button onClick={sendImage}>Send photo</button>
          </>
        ) : (
          <button onClick={capture}>Capture photo</button>
        )}
      </div>
    </div>
  );
};
export default CustomWebcam;
