// import useRef, useState, useCallback, useEffect
import { useRef, useState, useCallback, useEffect } from "react";
// import Webcam component
import Webcam from "react-webcam";

const CustomWebcam = () => {
  const webcamRef = useRef(null); // create a webcam reference
  const [imgSrc, setImgSrc] = useState(null); // initialize it

  // Captures Photo
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
  }, [webcamRef]);

  const retake = () => {
    setImgSrc(null);
  };

  /*-----------------------------------------------------------*/
  //New send photo function
  // This function will take the data of a jpg image and send it to an api
  // It will print the response from the api to the console
  function sendPhotos(data) {
    // Create a new FormData object
    let formData = new FormData();

    // Append the data to the form data with the name "photo"
    formData.append("photo", data);

    // Create a new XMLHttpRequest object for the api request
    let xhr = new XMLHttpRequest();

    // Specify the URL of the api endpoint
    let api = "https://sam141.brighton.domains.com/cloudvisionapi";

    // Open a POST request to the api
    xhr.open("POST", api);

    // Define what to do when the api request is successful
    xhr.onload = function () {
      // Check if the status code is 200 (OK)
      if (xhr.status === 200) {
        // Get the response text from the api
        let response = xhr.responseText;

        // Print the response to the console
        console.log(response);
      } else {
        // Print the status code and the error message to the console
        console.error(xhr.status + ": " + xhr.statusText);
      }
    };

    // Define what to do when the api request fails
    xhr.onerror = function () {
      // Print the error message to the console
      console.error("Network error");
    };

    // Send the form data to the api
    xhr.send(formData);
  }


  /*-----------------------------------------------------------*/
  // Function for sending photo
  const sendImage = async () => {
    try {

      // get the image file as a blob
      const responses = await fetch(imgSrc);
      const blob = await responses.blob();

      // create a FormData object and append the blob
      const formData = new FormData();
      formData.append("image", blob, "image.jpg");

      // send the request with the FormData as the body
      const response = await fetch("http://sam141.brighton.domains/cloudvisionapi", {
        method: "POST",
        body: formData,
      })
      // handle the response
      if (responses.ok) {
        console.log("Image sent successfully");
        console.log(responses);
      } else {
        console.error("Error sending image");
        console.log(responses);
      }

    } catch (error) {
      console.log("Error when sending image - error as follows:");
      console.log(error);
    };
  };

  return (
    <div className="container">
      {imgSrc ? (
        <img src={imgSrc} alt="webcam" />
      ) : (
        <Webcam
          //height={960}
          //width={540}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          screenshotQuality={0.8}
        />
      )}
      <div className="btn-container">
        <button>BrowsePhotos</button>
        {imgSrc ? (
          <>
            <button onClick={retake}>Retake photo</button>
            <button onClick={sendImage(imgSrc)}>Send photo</button>
          </>
        ) : (
          <button onClick={capture}>Capture photo</button>
        )}
      </div>
    </div>
  );
};
export default CustomWebcam;
