import { useCallback } from "react"; // import useRef
const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
}, [webcamRef]);

const retake = () => {
    setImgSrc(null);
};

export {
    capture,
    retake
};
