// Import the fs module to work with the file system
const fs = require('fs');

// Define a function that takes an image source as a parameter and saves it as a .jpg file
const saveScreenshot = (imageSrc) => {
  // Extract the base64 data from the image source
  const base64Data = imageSrc.replace(/^data:image\/\w+;base64,/, "");

  // Create a buffer from the base64 data
  const buffer = Buffer.from(base64Data, 'base64');

  // Generate a unique file name using the current date and time
  const fileName = `screenshot-${Date.now()}.jpg`;

  // Write the buffer to the file
  fs.writeFile(fileName, buffer, (err) => {
    // Handle any errors
    if (err) {
      console.error(err);
    } else {
      // Log a success message
      console.log(`Screenshot saved as ${fileName}`);
    }
  });
};

// Export the function so it can be imported in another file
module.exports = saveScreenshot;
