//Code Ref: https://blog.logrocket.com/using-react-webcam-capture-display-images/
import { useRef, useState, useCallback } from "react";
import Webcam from "react-webcam";
import { saveAs } from "file-saver";

const CustomWebcam = () => {
  const webcamRef = useRef(null); // create a webcam reference
  const [imgSrc, setImgSrc] = useState(null); // initialize it
  let imageTaken = false;
  // Captures Photo
  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
  }, [webcamRef]);

  const retake = () => {
    setImgSrc(null);
    imageTaken = false;
  };

  const saveImage = () => {
    if (imageTaken == true) {
      setImgSrc(null);
      const [imgSrc, setImgSrc] = useState(null); // initialize it

    }
    const imageSrc = webcamRef.current.getScreenshot();
    const blob = dataURItoBlob(imageSrc);
    saveAs(blob, "my-image.jpg");
  };

  // Convert data URI to binary array
  function dataURItoBlob(dataURI) {
    // Split the data URI into parts
    let parts = dataURI.split(",");
    // Get the content type (image/jpeg)
    let type = parts[0].split(":")[1].split(";")[0];
    // Decode the base64 data
    let data = atob(parts[1]);
    // Create a binary array
    let array = [];
    for (var i = 0; i < data.length; i++) {
      array.push(data.charCodeAt(i));
    }
    // Return a Blob object
    return new Blob([new Uint8Array(array)], { type: type });
  }

  // Function for sending photo
  async function sendImage(imageSrc) {
    const runFunction = false;
    if (runFunction === true) {
      try {

        // get the image file as a blob
        const responses = await fetch(imageSrc);
        const blob = await responses.blob();

        // create a FormData object and append the blob
        const formData = new FormData();
        formData.append("image", blob, "image.jpg");

        // send the request with the FormData as the body
        const response = await fetch("http://sam141.brighton.domains/cloudvisionapi", {
          method: "POST",
          body: formData,
        })
        // handle the response
        if (response.ok) {
          console.log("Image sent successfully");
          console.log(response);
        } else {
          console.error("Error sending image - response as follows:");
          console.log(response);
        }

      } catch (error) {
        console.log("Error when sending image - error as follows:");
        console.log(error);
      };
    } else {
      console.log('Not currently sending responses');
    }
  };

  return (
    <div className="container">
      {imgSrc ? (
        <img src={imgSrc} alt="webcam" />
      ) : (
        <Webcam
          //height={960}
          //width={540}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          screenshotQuality={0.8}
        />
      )}
      <div className="btn-container">
        <button onClick={saveImage}>Save photo</button>
        {imgSrc ? (
          <>
            <button onClick={retake}>Retake photo</button>
          </>
        ) : (
          <button onClick={capture}>Capture photo</button>
        )}
        <button onClick={sendImage}>Send photo</button>
      </div>
    </div>
  );
};
export default CustomWebcam;
